import React from "react";
import { useState } from 'react';
import "../Styles/SearchBar.css";
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from "@mui/icons-material/Close";
import Block from "./Block";
import axios from 'axios';


function SearchBar({ placeholder, data }) {
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");
  const [dataResult, setDataResult] = useState("");

  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);

  };

  const clearInput = () => {
    setFilteredData([]);
    setWordEntered("");
    setDataResult([]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (wordEntered === "") {
      setFilteredData([]);
      event.preventDefault(); // prevent the default form submission behavior
      window.location.reload(); // reload the page
    } else {
      setFilteredData(wordEntered.toLowerCase());
      axios
        .get(`http://localhost:3002/search/${wordEntered.toLowerCase()}`)
        .then((response) => {

          setDataResult(response.data); // set the state with the response data
        })
        .catch((err) => console.log("ERROR ", err))
    }
  };

  return (
    <div className="search">
     <div className="searchInputs">
  <input
    type="text"
    placeholder={placeholder}
    value={wordEntered}
    onChange={handleFilter}
  />


  <button className="submit-button" onClick={handleSubmit}>Submit</button>
  <div className="searchIcon">
    {filteredData.length === 0 ? (
      <SearchIcon />
    ) : (
      <CloseIcon id="clearBtn" onClick={clearInput} />
    )}
  </div>
</div>

      <div className="dataResult">
        {

        dataResult && dataResult.map(d =>
          <Block title={d.title} image={d.body} url={d.url}/>
        )
      }
      </div>
    </div>
  );
}

export default SearchBar;
